<?phpuse SharePilotV2\Components\Database;use SharePilotV2\Models\Users;use SharePilotV2\Components\ResponseHandler;use SharePilotV2\Components\RequestHandler;use SharePilotV2\Components\UserAuth;class Controller{    public function post(){    }    public function get(){    }    public function updateuser(){        $user = new Users();        $user->name = RequestHandler::get("name");        $user->lastname = RequestHandler::get("lastname");        $user->email = RequestHandler::get("email");        $user->role = 1;        $user->password = password_hash(RequestHandler::get("password"), PASSWORD_DEFAULT);        $user->mobilephone = RequestHandler::get("mobilephone");        ResponseHandler::respond($user->update()->where("id","=",RequestHandler::get("id"))->execute());    }    public function registeruser(){        $user = new Users();        $user->name = RequestHandler::get("name");        $user->lastname = RequestHandler::get("lastname");        $user->email = RequestHandler::get("email");        $user->role = 1;        $user->password = RequestHandler::get("password");        $user->mobilephone = RequestHandler::get("mobilephone");        $UserAUth = new UserAUth(Database::getInstance());        $result = $UserAUth->register($user);        ResponseHandler::respond($result);    }    public function getusers(){        $users = new Users();        ResponseHandler::respond($users->select()->fields("id","name","lastname","email","mobilephone")->execute());    }    public function deleteuser(){        $user = new Users();        $result = $user->delete()->where("id","=",RequestHandler::get("id"))->execute();        ResponseHandler::respond($result);    }    public function getuserbyid(){        $users = new Users();        ResponseHandler::respond($users->select()->fields("id","name","lastname","email","mobilephone")->where("id","=", RequestHandler::get("id"))->execute()[0]);    }}