<div class="w3-row w3-padding-64">    <div class="w3-container w3-teal">        <h1>Settings</h1>    </div>    <div class="w3-bar w3-black">        <button class="w3-bar-item w3-button" onclick="openCity('Users')">Users</button>        <button class="w3-bar-item w3-button" onclick="openCity('Locale')">Locale</button>    </div>    <div id="Users" class="w3-container city">        <h2>Manage Users</h2>        <form class="w3-container">            <label class="w3-text-teal"><b>Name</b></label>            <input class="w3-input w3-border w3-light-grey" type="text" id="name">            <label class="w3-text-teal"><b>Last Name</b></label>            <input class="w3-input w3-border w3-light-grey" type="text" id="lastname">            <label class="w3-text-teal"><b>Email</b></label>            <input class="w3-input w3-border w3-light-grey" type="text" id="email">            <label class="w3-text-teal"><b>Password</b></label>            <input class="w3-input w3-border w3-light-grey" type="text" id="password">            <label class="w3-text-teal"><b>MobilePhone</b></label>            <input class="w3-input w3-border w3-light-grey" type="text" id="mobilephone">            <br>            <input type="button" class="w3-btn w3-blue-grey" value="Register" onclick="register()">        </form>    </div>    <div id="Locale" class="w3-container city" style="display:none">        <h2>Set Locale</h2>        <p>Set the locale</p>    </div></div><script>    function openCity(cityName) {        var i;        var x = document.getElementsByClassName("city");        for (i = 0; i < x.length; i++) {            x[i].style.display = "none";        }        document.getElementById(cityName).style.display = "block";    }    function register(){        alert("registering user");    }</script>